    public function getSourceContext(): Source
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Connexion{% endblock %}

{% block body %}
    <h1>Connexion</h1>

    <div class=\"container\">
        {# Formulaire de connexion #}
        <form action=\"{{ path('signin') }}\" method=\"post\">
            <div class=\"form-group\">
                <label for=\"username\">Email :</label>
                <input type=\"text\" id=\"username\" name=\"_username\" value=\"{{ last_username }}\" class=\"form-control\" required />
            </div>

            <div class=\"form-group\">
                <label for=\"password\">Mot de passe :</label>
                <input type=\"password\" id=\"password\" name=\"_password\" class=\"form-control\" required />
            </div>

            <button type=\"submit\" class=\"btn btn-primary\">Se connecter</button>
        </form>

        {# Affichage des erreurs de connexion #}
        {% if error %}
            <div class=\"alert alert-danger mt-3\">
                {{ error.messageKey|trans(error.messageData, 'security') }}
            </div>
        {% endif %}
    </div>

    <p>Vous n'avez pas encore de compte ? <a href=\"{{ path('signup') }}\">S'inscrire</a></p>
{% endblock %}
", "user/signin.html.twig", "C:\\Users\\marwa\\biblioverse\\templates\\user\\signin.html.twig");
